import { MicroBehavior } from "@/components/add-habit/types"

const API_URL = '/api/behavior-suggestions'

export interface BehaviorSuggestionRequest {
  habit: {
    title: string
    description: string
  }
}

export interface BehaviorSuggestionResponse {
  suggestions: {
    title: string
    description: string
  }[]
}

export async function getBehaviorSuggestions(
  habitTitle: string,
  habitDescription: string
): Promise<MicroBehavior[]> {
  const requestData: BehaviorSuggestionRequest = {
    habit: {
      title: habitTitle,
      description: habitDescription,
    },
  }

  try {
    const response = await fetch(API_URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(requestData),
    })

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}))
      throw new Error(errorData.error || `APIè¯·æ±‚å¤±è´¥: ${response.status} ${response.statusText}`)
    }

    const data: BehaviorSuggestionResponse = await response.json()

    if (!data.suggestions || !Array.isArray(data.suggestions)) {
      throw new Error('APIè¿”å›æ•°æ®æ ¼å¼é”™è¯¯')
    }

    // è½¬æ¢APIæ•°æ®ä¸ºç»„ä»¶éœ€è¦çš„æ ¼å¼
    return data.suggestions.map((suggestion, index) => ({
      id: (index + 1).toString(), // ç®€å•é€’å¢æ•°å­—ID
      title: suggestion.title,
      description: suggestion.description,
      selected: false,
    }))
  } catch (error) {
    console.error('è·å–ä¹ æƒ¯å»ºè®®å¤±è´¥:', error)
    throw error
  }
}

